/* Rain + lightning */
(function initWeather(){
  const canvas = document.getElementById("rain");
  const ctx = canvas.getContext("2d");
  let w=0,h=0, drops=[];
  function resize(){
    w = canvas.width = window.innerWidth * devicePixelRatio;
    h = canvas.height = window.innerHeight * devicePixelRatio;
    drops = Array.from({length: Math.floor((window.innerWidth*window.innerHeight)/18000)}, () => ({
      x: Math.random()*w,
      y: Math.random()*h,
      l: (Math.random()*18+10)*devicePixelRatio,
      s: (Math.random()*18+14)*devicePixelRatio,
    }));
  }
  window.addEventListener("resize", resize, {passive:true});
  resize();

  function tick(){
    ctx.clearRect(0,0,w,h);
    ctx.globalAlpha = 0.55;
    ctx.lineWidth = 1.2*devicePixelRatio;
    for(const d of drops){
      ctx.beginPath();
      ctx.moveTo(d.x, d.y);
      ctx.lineTo(d.x, d.y + d.l);
      ctx.strokeStyle = "rgba(200,220,255,.55)";
      ctx.stroke();
      d.y += d.s;
      d.x += 0.6*devicePixelRatio;
      if(d.y > h){ d.y = -20; d.x = Math.random()*w; }
      if(d.x > w+50){ d.x = -20; }
    }
    requestAnimationFrame(tick);
  }
  tick();

  const flash = document.getElementById("lightning");
  function doFlash(){
    const bursts = Math.random() < 0.25 ? 2 : 1;
    let i = 0;
    function burst(){
      i++;
      flash.style.background = "rgba(255,255,255,.22)";
      setTimeout(()=> flash.style.background = "rgba(255,255,255,0)", 90);
      if(i < bursts){
        setTimeout(burst, 180 + Math.random()*220);
      }
    }
    burst();
    setTimeout(doFlash, 2600 + Math.random()*5200);
  }
  setTimeout(doFlash, 1200);
})();

