function escapeHtml(s){
  return String(s)
    .replaceAll("&","&amp;")
    .replaceAll("<","&lt;")
    .replaceAll(">","&gt;")
    .replaceAll('"',"&quot;")
    .replaceAll("'","&#039;");
}

function renderScene(){
  const id = state.scene;
  const scene = SCENES[id];
  if(!scene){
    sceneTitle.textContent = `Cena ${id} (não encontrada)`;
    sceneText.textContent = "Esta cena não foi encontrada no arquivo importado.";
    choicesEl.innerHTML = "";
    return;
  }
  sceneTitle.textContent = `Cena ${id}`;
  sceneMeta.textContent = "Escolha com cuidado: não dá pra voltar.";
  sceneText.textContent = scene.text;

  // Media (imagem por cena)
  const oldMedia = document.getElementById("sceneMedia");
  if(oldMedia) oldMedia.remove();
  const media = SCENE_MEDIA[id];
  if(media && media.image){
    const fig = document.createElement("figure");
    fig.className = "sceneMedia";
    fig.id = "sceneMedia";
    const img = document.createElement("img");
    img.src = media.image;
    img.alt = media.alt || "";
    img.loading = "lazy";
    fig.appendChild(img);
    if(media.caption){
      const cap = document.createElement("figcaption");
      cap.textContent = media.caption;
      fig.appendChild(cap);
    }
    // insere antes do texto da cena
    sceneText.parentElement.insertBefore(fig, sceneText);
  }

  choicesEl.innerHTML = "";
  const choices = Array.isArray(scene.choices) ? scene.choices : [];
  if(choices.length === 0){
    const end = document.createElement("div");
    end.className = "muted";
    end.textContent = "Sem escolhas explícitas nesta cena. (Talvez seja uma cena de fim ou continuação.)";
    // Fallback: allow manual jump by scene number (useful when the text format doesn't contain explicit "(vá para X)")
    const wrap = document.createElement("div");
    wrap.style.display = "flex";
    wrap.style.gap = "10px";
    wrap.style.marginTop = "10px";
    wrap.style.flexWrap = "wrap";

    const inp = document.createElement("input");
    inp.type = "number";
    inp.min = "1";
    inp.placeholder = "Digite a cena...";
    inp.style.padding = "12px 12px";
    inp.style.borderRadius = "12px";
    inp.style.border = "1px solid rgba(255,255,255,.12)";
    inp.style.background = "rgba(0,0,0,.35)";
    inp.style.color = "white";
    inp.style.fontFamily = "ui-monospace, SFMono-Regular, Menlo, Monaco, Consolas, \"Liberation Mono\", \"Courier New\", monospace";
    inp.style.width = "220px";

    const go = document.createElement("button");
    go.type = "button";
    go.className = "btn btn--primary";
    go.textContent = "Ir para";
    go.addEventListener("click", () => {
      const n = Number(inp.value);
      if(!Number.isFinite(n) || n<=0) return alert("Digite um número de cena válido.");
      if(!SCENES[n]) return alert("Essa cena não existe no arquivo importado.");
      goToScene(n);
    });

    wrap.appendChild(inp);
    wrap.appendChild(go);
    choicesEl.appendChild(wrap);

    choicesEl.appendChild(end);
    return;
  }

  for(const ch of choices){
    const btn = document.createElement("button");
    btn.type = "button";
    btn.className = "btn choiceBtn";
    btn.innerHTML = `<b>${escapeHtml(ch.text)}</b><small>Ir para ${ch.to}</small>`;
    btn.addEventListener("click", () => goToScene(ch.to));
    choicesEl.appendChild(btn);
  }
}

function checkDeath(){
  if(state.stamCur != null && state.stamCur <= 0){
    state.stamCur = 0;
    pushLog(state, "☠️ Você morreu (RESISTÊNCIA chegou a 0).");
    alert("☠️ Você morreu (RESISTÊNCIA chegou a 0).\n\nAperte Recomeçar para iniciar de novo.");
    saveState(state);
    renderHUD();
    return true;
  }
  if(state.fearMax != null && state.fearCur >= state.fearMax){
    state.fearCur = state.fearMax;
    pushLog(state, "😱 Você morreu de medo (MEDO atingiu o máximo).");
    alert("😱 Você morreu de medo (MEDO atingiu o máximo).\n\nAperte Recomeçar para iniciar de novo.");
    saveState(state);
    renderHUD();
    return true;
  }
  return false;
}

function goToScene(id){
  if(checkDeath()) return;
  state.scene = id;
  pushLog(state, `→ Cena ${id}`);
  saveState(state);
  renderHUD();
  renderScene();
  setDiceLog(state);
  window.scrollTo({top:0, behavior:"smooth"});
}

/* Rolls */
function updateSkillDerived(){
  if(state.skillMax == null) return;
  const unarmed = Math.max(0, state.skillMax - 3);
  state.skillCur = unarmed;
  inpSkillCur.value = unarmed;
}

rollSkill.addEventListener("click", () => {
  const r = d6();
  state.skillMax = 6 + r;
  updateSkillDerived();
  inpSkillMax.value = state.skillMax;
  pushLog(state, `HABILIDADE: 6 + d6(${r}) = ${state.skillMax} → desarmado: ${state.skillCur}`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
});

rollStam.addEventListener("click", () => {
  const r = d6();
  state.stamMax = 12 + r;
  state.stamCur = state.stamMax;
  inpStamMax.value = state.stamMax;
  inpStamCur.value = state.stamCur;
  pushLog(state, `RESISTÊNCIA: 12 + d6(${r}) = ${state.stamMax}`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
});

rollLuck.addEventListener("click", () => {
  const r = d6();
  state.luckMax = 6 + r;
  state.luckCur = state.luckMax;
  inpLuckMax.value = state.luckMax;
  inpLuckCur.value = state.luckCur;
  pushLog(state, `SORTE: 6 + d6(${r}) = ${state.luckMax}`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
});

rollFear.addEventListener("click", () => {
  const r = d6();
  state.fearMax = 6 + r;
  state.fearCur = state.fearCur ?? 0;
  inpFearMax.value = state.fearMax;
  inpFearCur.value = state.fearCur;
  pushLog(state, `MEDO máx: 6 + d6(${r}) = ${state.fearMax}`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
});

inpName.addEventListener("input", () => {
  state.name = inpName.value.trim();
  saveState(state);
  renderHUD();
});

btnStart.addEventListener("click", () => {
  state.name = inpName.value.trim();
  if(!state.name) return alert("Digite seu nome.");
  if(state.skillMax==null || state.stamMax==null || state.luckMax==null || state.fearMax==null){
    return alert("Role os dados de HABILIDADE, RESISTÊNCIA, SORTE e MEDO máximo antes de começar.");
  }
  state.scene = 0;
  pushLog(state, "▶️ Aventura iniciada no Prólogo (Cena 0).");
  saveState(state);
  showGame();
});

/* Utilities */
btnTestLuck.addEventListener("click", () => {
  if(state.scene==null) return;
  if(state.luckCur==null) return;
  if(state.luckCur<=0) return alert("Sua SORTE atual já está em 0.");

  const roll = d2d6();
  const hadLuck = roll <= state.luckCur;
  state.luckCur = Math.max(0, state.luckCur - 1);
  pushLog(state, `🎲 Teste de Sorte: 2d6=${roll} → ${hadLuck ? "SORTE!" : "AZAR!"} (SORTE agora ${state.luckCur})`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
  alert(hadLuck ? "Você teve SORTE!" : "Você teve AZAR!");
});

btnAddFear.addEventListener("click", () => {
  if(state.fearCur==null) state.fearCur=0;
  state.fearCur += 1;
  pushLog(state, "😰 +1 MEDO");
  saveState(state);
  setDiceLog(state);
  renderHUD();
  checkDeath();
});

btnHeal.addEventListener("click", () => {
  if(state.stamCur==null || state.stamMax==null) return;
  const before = state.stamCur;
  state.stamCur = clamp(state.stamCur + 2, 0, state.stamMax);
  pushLog(state, `❤️ +2 VIGOR (RESISTÊNCIA ${before} → ${state.stamCur})`);
  saveState(state);
  setDiceLog(state);
  renderHUD();
});

/* Rules dialog */
btnRules.addEventListener("click", () => dlgRules.showModal());
btnCloseRules.addEventListener("click", () => dlgRules.close());

/* Restart */
btnRestart.addEventListener("click", () => {
  const ok = confirm("Recomeçar apaga seu progresso salvo neste navegador. Tem certeza?");
  if(!ok) return;
  clearState();
  state = defaultState();
  showCreate();
});

/* Bootstrap */
(function init(){
  if(state && state.scene != null && hasCompleteSheet(state)){
    showGame();
  } else {
    showCreate();
  }
})();
